import React, { useState } from "react";
import Button from '../../../components/atoms/button';

import Input from "../../../components/atoms/input/index.js";
import styles from './partials.module.scss' ;

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Signup() {
    const [email,setEmail]=useState('');
    const [name,setName]=useState('');
    const[password,setPassowrd]=useState('');
    const handleSingnUp=()=>{
        if (!email.length && !password.length && !name.length) {
            toast.error("Some Required fields are missing");
          }
            fetch("http://localhost:3001/api/users/signup",{
        body:{
            email,
            name,
            password
        },
        method: 'POST'
    }).then((res)=>res.json()).then((data)=>{
        console.log({data});
        toast.success("User signed up successfully")
    }).catch(error=>{
        console.log(error);
        toast.error("something went wrong")
    })
    };
  return (
    <div className={styles.form}>
                <Button 
                text="Join with google"
                icon="ri:google-fill"
                className={styles.google}
                />
                <div className={styles.options}>
                    <span>or join with Email address and Password</span>
                </div>
                <article className={styles.details}>
                    <Input 
                        title="Email"
                        type="email"
                        value={email}
                        placeholder={ "Type your Email"}
                        onChange={(e)=>{setEmail(e.target.value)}}
                    />
                    <Input 
                        title="Full Name"
                        type="name"
                        value={name}
                        placeholder={ "Full Name"}
                        onChange={(e)=>{setName(e.target.value)}}
                    />
                    <Input 
                         title="Password"
                        type="password"
                        value={password}
                        placeholder={ "Type your Password" }
                        onChnage={(e)=>{setPassowrd(e.target.value)}}
                    />
                </article>
                <Button 
                text="Join with Email"
                icon="material-symbols:login"
                className={styles.EmailBtn}
                handleclick={handleSingnUp}
                />
            </div>
  )
}

export default Signup;
