import React, { useState, useEffect } from 'react';
import styles from './note.module.scss';
import formatDate from '../../../utils/formatDate';
import Button from '../../atoms/button';

import { useNavigate } from 'react-router-dom';
import utils from '../../../utils/localstorage';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Notes from '../../../../../backend/src/models/notes.model';

function Note(props) {
    const { text, date, color } = props;
    const [expand, setExpand] = useState(false);
    const [noteText, setNoteText] = useState(text || '');
    const navigate = useNavigate();
    const authToken = utils.getFromLocalStorage('auth_key');
    
    const handleSave = () => {
        
        if (!authToken) {
            toast.error("Unauthorized access!");
            return;
        }
        if (!noteText.length || noteText.split(' ').length < 2) {
            toast.error("Note should contain at least two words");
            return;
        }
        fetch("http://localhost:3001/api/notes", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                authorization: authToken,
            },
            body: JSON.stringify({
                text: noteText,
                color
            })
        })
        .then((res) => res.json())
        .then((data) => {
            console.log({ data });
            if (data?.success===200) {
                toast.success("New note created successfully");
            } else {
                toast.error(data?.message);
            }
        })
        .catch(error => {
            console.log({ error });
            toast.error("Something went wrong");
        });



    };

    async function handleupdate(req, res) {
        const { text, color } = req.body; // Add 'color' if you want to update the color as well
        const noteId = req.params.noteId;
        const updates = { text, color, updatedAt: Date.now() };
      
        try {
          const note = await Notes.findByIdAndUpdate(noteId, updates, { new: true });
          if (!note) {
            return res.status(404).json({ success: 404, message: 'Note not found' });
          }
          if (note.userId.toString() !== req.userId) {
            return res.status(403).json({ success: 403, message: 'Unauthorized to update this note' });
          }
          res.json({ success: 200, data: note });
        } catch (err) {
          console.error('Error updating note:', err);
          res.status(500).json({ success: 500, message: 'Error updating note' });
        }
      }

    return (
        <article className={styles.container} style={{ backgroundColor: color }}>
           
            <div className={`${styles.content}`}>
                {text === "Create New Note..."? (
                    <textarea 
                        value={noteText} 
                        className={styles.textarea}
                        onChange={(e) => setNoteText(e.target.value)}
                    />
                ) : (
                    <>
                        <p className={`${expand ? styles.expanded : ""}`}>{text}</p>
                        {text.length > 154 ? (
                            <button onClick={() => setExpand(prev => !prev)}>
                                {expand ? "Read less" : "Read more"}
                            </button>
                        ) : null}
                    </>
                )}
            </div>
            <footer className={styles.footer}>
                <div>
                    {formatDate(date)}
                </div>
                {noteText==="Create New Note..." ? (
                    <Button 
                        text={'save'} 
                        className={styles.saveBtn}
                        handleclick={handleupdate}
                    />
                ) : <Button 
                text={'Update'}  
                className={styles.saveBtn}
                handleclick={handleSave}
            />}
            </footer>
        </article>
    );
}

export default Note;
