import React, { useEffect, useState } from 'react'
import styles from './notes.module.scss'
import Wrapper from '../../components/HOC/wrapper'
import Greeting from '../../components/atoms/greeting'
import Note from '../../components/cards/note'
import notesData from '../../data/notes.json'
import utils from '../../utils/localstorage.js'
import types from '../../config/types.js'

function Notes() {
  const[notesCollection,setNotesCollection]=useState([]);
  
  useEffect(() => {
    //to prevent an empty screen at first
    const data=utils.getFromLocalStorage(types.NOTES_DATA);
    if(data && data.length) {
      setNotesCollection(notesData)
      return;
    }
    utils.addToLocalStorage('notes-data',notesData);
    setNotesCollection(notesData)
  }, [])
  
  return (
    <section className={styles.container}>
        <Greeting />
        <main>
          {notesCollection.map((note,i)=><Note key={note.id} text={note.text} date={note.createdAt} color={note.color}/>)}
        </main>
    </section>
  )
}

export default Wrapper(Notes)
