import React, { useState } from 'react';
import styles from './note.module.scss';
import formatDate from '../../../utils/formatDate';
import Button from '../../atoms/button';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';
import utils from '../../../utils/localstorage'

function Note(props) {

    const { text, date, color } = props;
    const [expand, setExpand] = useState(false);
    const [noteText,setNoteText] =useState( "" );     
    const navigate=useNavigate();
    const authToken=utils.getFromLocalStorage('auth_key')
    const handleSave=(() => {
        //logic for creating a note
        if(!authToken) toast.error("Unauthorized access denied!");
        if (!text.length && text.split(' ').length || !color) {
            toast.error("Note should contain a least two words");
            return;
          }
        fetch("http://localhost:3001/api/notes",{
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                "authentication":authToken
            },
            body: JSON.stringify({
                text,
                color
            })
    })
    .then((res)=>res.json())
    .then((data)=>{
        console.log({data});
    if(data?.success===200){
        toast.success("New note created successfully") ;
        navigate('/notes');
        
    }
    else toast.error(data?.message);

    })
    .catch(error=>{
        console.log({error});
        toast.error("User login failed!")
    })
    })
    return (
        <article className={styles.container} style={{ backgroundColor: color }}>
            <div className={`${styles.content}`}>
            
         {text.length===1 ?
             (<textarea value={noteText} onChange={(e)=>setNoteText(e.target.value) }className={styles.textarea}/>) : 
             ( <>
                <p className={`${expand ? styles.expanded : ""}`}>{text}</p>
                {text.length>154?(
                    <button onClick={() => setExpand(prev => !prev)}>
                    {expand ? "Read less" : "Read more"}
                </button>
                ):null}
                </>
                )
           
               
    } 
            </div>
            <footer className={styles.footer}>
                <div>
                {formatDate(date)}
                </div>
                {noteText.length?<Button 
                    text={'save'} 
                    className={styles.saveBtn}
                    handleClick={handleSave}
                />:null}
            </footer>
        </article>
    );
}

export default Note;
